require 'spec_helper'

describe OpenAssets::Transaction::SpendableOutput, :network => :testnet do

  it 'to hash' do
    issue_spec = Bitcoin::Tx.parse_from_payload(''.htb)
    transfer_spec = Bitcoin::Tx.parse_from_payload(''.htb)
    metadata = OpenAssets::Protocol::MarkerOutput.parse_from_payload(
        OpenAssets::Protocol::MarkerOutput.parse_script(issue_spec.outputs[1].script_pubkey).htb).metadata
    output = OpenAssets::Protocol::TransactionOutput.new(
        600, transfer_spec.outputs[2].script_pubkey, 'oK31ByjFuNhfnFuRMmZgchsdiprYmRzuz5', 9900, OpenAssets::Protocol::OutputType::TRANSFER, metadata)
    output.account = ''
    result = OpenAssets::Transaction::SpendableOutput.new(
        Bitcoin::OutPoint.new('2ef6aaf051229ff755a137a51466b54da6d8c87d17130bca8a879e9e64172ebd'.rhex, 2), output)
    result.confirmations = 20842
    expect(result.to_hash).to match(
                                  'txid' => '2ef6aaf051229ff755a137a51466b54da6d8c87d17130bca8a879e9e64172ebd',
                                  'vout' => 2,
                                  'address' => 'mkgW6hNYBctmqDtTTsTJrsf2Gh2NPtoCU4',
                                  'oa_address' => 'bWvePLsBsf6nThU3pWVZVWjZbcJCYQxHCpE',
                                  'script' => '76a91438a6ebdf20cae2c9287ea014464042112ea3dbfd88ac',
                                  'script_type' => 'pubkeyhash',
                                  'amount' => '0.00000600',
                                  'confirmations' => 20842,
                                  'asset_id' => 'oK31ByjFuNhfnFuRMmZgchsdiprYmRzuz5',
                                  'account' => '',
                                  'asset_quantity' => '9900',
                                  'asset_amount' => '990.0',
                                  'asset_definition_url' => 'https://goo.gl/uVgst4',
                                  'proof_of_authenticity' => false,
                                  'output_type' => 'transfer')
  end

end